mapper.update:
UPDATE REVIEW_BOARD SET
TITLE = #{title},
CONTENT = #{content},
IMGNAME = #{imgName}
WHERE WRITE_NO=#{writeNo}

mapper.select:
SELECT COUNT(*)
FROM
REVIEW_LIKE
WHERE LIKE_ID = #{likeId}
AND LIKE_WRITE_NO = #{writeNo}

mapper.update:
UPDATE REVIEW_BOARD SET
LIKES = LIKES + 1
WHERE WRITE_NO=#{writeNo}

UPDATE REVIEW_BOARD SET
LIKES = LIKES - 1
WHERE WRITE_NO=#{writeNo}

mapper.delete:
DELETE FROM REVIEW_LIKE
WHERE LIKE_ID = #{likeId}
AND
LIKE_WRITE_NO = #{writeNo}
